export default {
	add_emp : async ()=>{
			if(pers.selectedRow.poste == ""){
			showAlert("veillez remplir les champs svp");
		}else{
			await add_employee.run();
		await afficher_liste.run();
		showAlert("vous avez ajouter un employer avec success");
		return closeModal('modal_add_empl');			
		}



	},
	update_emp: async ()=>{
		await modifier_employee.run();
		await afficher_liste.run();
		return closeModal('modal_modifier');

	},

	add_affectation : async ()=>{
			if(input_affect.text == ""){
			showAlert("veillez remplir les champs svp");
		}else{
			await add_affectation.run();
		await affiche_affectation.run();
		showAlert("vous avez ajouter une affectation avec success");
		return closeModal('Modal_ajouter_affectation');			
		}
}
}
